const price=document.querySelector(".switch-block"),priceBtn=document.querySelector(".switch-btn"),percent=.1;let month;price.addEventListener("click",()=>{(priceBtn.classList.contains("switch-on")?(priceBtn.classList.remove("switch-on"),percentForAnnually):(priceBtn.classList.add("switch-on"),percentForMonth))()});const percentBlock=document.querySelector(".price-container");function percentForMonth(){percentBlock&&percentBlock.querySelectorAll(".switch-item").forEach(e=>{e.querySelectorAll(".switch-item__price-persent").forEach(e=>{month=Math.round(e.getAttribute("data-value")*percent)-.01,e.innerHTML=+month})})}function percentForAnnually(){percentBlock.querySelectorAll(".switch-item").forEach(e=>{e.querySelectorAll(".switch-item__price-persent").forEach(e=>{month=0,month=Math.round(e.getAttribute("data-value")-e.getAttribute("data-value")*percent)-.01,e.innerHTML=+month})})}priceBtn.classList.contains("switch-on")&&percentForMonth();